-- Create "gpt_dialogs" table
CREATE TABLE "gpt_dialogs" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "peer_id" character varying NOT NULL, "prompt_msg_id" bigint NOT NULL, "prompt_msg" character varying NOT NULL, "gpt_msg_id" bigint NOT NULL, "gpt_msg" character varying NOT NULL, "thread_top_msg_id" bigint NULL, "created_at" timestamptz NOT NULL, PRIMARY KEY ("id"));
-- Create index "gptdialog_peer_id_thread_top_msg_id" to table: "gpt_dialogs"
CREATE INDEX "gptdialog_peer_id_thread_top_msg_id" ON "gpt_dialogs" ("peer_id", "thread_top_msg_id");
