-- Create "repositories" table
CREATE TABLE "repositories" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "owner" character varying NOT NULL, "name" character varying NOT NULL, "full_name" character varying NOT NULL, "html_url" character varying NOT NULL, "description" character varying NOT NULL DEFAULT '', "last_pushed_at" timestamptz NULL, "last_event_at" timestamptz NULL, PRIMARY KEY ("id"));
